mapper.select:
select * from( select rownum rnum, a.* from( select app_no
, to_char(enroll_date, 'YYYY-MM-DD') as "enroll_date" , app_tstatus , title , b.emp_no , form_name , form_code , app_amount , app_sequence , emp_name from approval b join employee e on (b.emp_no = e.emp_no) where b.status = '1' and app_tstatus = '진행중' and app_no = ( select app_no from app_line where ref_whether = 'N' and app_status ='미결재' and emp_no = #{empNo} ) order by b.enroll_date
)a ) where rnum between 1 and 4
