mapper.insert:
insert
  into board
  (
  	board_no
  , board_cno
  , board_title
  , board_writer
  , board_content
  , board_pin
  ) 
  values
  (
  	seq_bno.nextval
  , #{boardCno}
  , #{boardTitle}
  , #{boardWriter}
  , #{boardContent}
  , #{boardPin}
  )

mapper.update:
update
       board
   set board_cno = #{boardCno}
     , board_title = #{boardTitle}
     , board_content = #{boardContent}
     , board_pin = #{boardPin}
 where board_no = #{boardNo}

