mapper.update:
update 
	   commute
   set start_time = #{startTime}
 where emp_no = #{empNo}
   and enroll_date = #{enrollDate}

update 
	   commute
   set end_time = #{endTime}
 where emp_no = #{empNo}
   and enroll_date = #{enrollDate}

update
       employee
   set status = #{status}
 where emp_no = #{empNo}
