mapper.insert:
insert
  into data_board
  (
  	db_no
  , db_title
  , db_writer
  , db_content
  , origin_name
  , change_name
  )
  values
  (
  	seq_dbno.nextval
  , #{dbTitle}
  , #{dbWriter}
  , #{dbContent}
  , #{originName}
  , #{changeName}
  )

mapper.update:
update
	   data_board
   set db_title = #{dbTitle}
     , db_content = #{dbContent}
     , origin_name = #{originName}
     , change_name = #{changeName}
 where db_no = #{dbNo}
