mapper.insert:
	insert 
	  into mail_tag
  	  (
  	    tag_no
  	  , emp_no
  	  , tag_name
  	  , tag_color  
  	  )
	  values
	  (
	    seq_tagno.nextval
	  , #{empNo}
	  , #{tagName}
	  , #{tagColor}
	  )

insert 
  into send_mail
  (
  	MAIL_NO
  , SEND_MAIL_ADDRESS
  , MAIL_TITLE
  , MAIL_CONTENT
  )
  values
  (
    seq_mano.nextval
  , #{ sendMailAdd }
  , #{ mailTitle }
  , #{ mailContent }
  )

insert
  into rec_mail
  (
	RECEP_MAIL_NO
  , MAIL_NO
  , RECEP_MAIL_ADDRESS
  , SEND_MAIL_ADDRESS
  , MAIL_TITLE
  , MAIL_CONTENT
  , to_char(REC_DATE , 'yyyy-mm-dd HH24:MM') "REC_DATE"
  , IMPOR_MAIL
  , REFERENCE
  , HIDDEN_REFERENCE
  )
  values
  (
    seq_recmano.nextval
  , seq_mano.currval
  , #{ recMailAdd }
  , #{ sendMailAdd }
  , #{ mailTitle }
  , #{ mailContent }
  , to_date(sysdate, 'YYYY-MM-DD HH24:MM')
  , #{ imporMail }
  , #{ reference }
  , #{ hiddenReference }
  )
