mapper.insert:
insert
  into my_calendar
  (
  	cal_no
  , emp_no
  , cal_title
  , cal_color
  )
  values
  (
  	seq_calno.nextval
  , #{empNo}
  , #{calTitle}
  , #{calColor}
  )

insert
  into schedule
  (
  	sc_no
  , sc_writer
  , cal_no
  , sc_title
  , sc_place
  , sc_content
  , start_date
  , end_date
  , start_time
  , end_time
  , sc_allday
  )
  values
  (
  	seq_scno.nextval
  , #{scWriter}
  , #{calNo}
  , #{scTitle}
  , #{scPlace}
  , #{scContent}
  , #{startDate}
  , #{endDate}
  , #{startTime}
  , #{endTime}
  , #{allDay}
  )

mapper.update:
update
	   my_calendar
   set cal_title = #{calTitle}
     , cal_color = #{calColor}
 where cal_no = #{calNo}

update
	   schedule
   set sc_title = #{scTitle}
  	 , sc_place = #{scPlace}
  	 , sc_content = #{scContent}
  	 , start_date = #{startDate}
  	 , end_date = #{endDate}
  	 , start_time = #{startTime}
  	 , end_time = #{endTime}
  	 , sc_allday = #{allDay}
 where sc_no = #{scNo}

mapper.insert:
insert
  into sc_attendee
  (
  	sc_no
  , emp_no
  )
  values
  (
  	#{scNo}
  , #{empNo}
  )
